mapper.select:
/* User.xml - selectBjRoom */
select
    t1.room_no as roomNo
    , t1.title as title
    , t1.start_date as startDt
    , 3 as auth
from tb_broadcast_room t1
where t1.mem_no = #{value}
    and t1.state not in (4, 5);

/* User.xml - selectDisconnectRoom */
select
    t1.room_no as roomNo
    , t1.title as title
    , t1.start_date as startDt
    , 3 as auth
from tb_broadcast_room t1
where t1.mem_no = #{value}
    and t1.state = 5;

mapper.update:
/* User.xml - updateNormalRoom */
update tb_broadcast_room set
  state = 1
where room_no = #{value}

mapper.select:
/* User.xml - selectJoinRoom */
select
    t1.room_no as roomNo
    , t1.title as title
    , t1.start_date as startDt
    , t2.auth
from tb_broadcast_room t1 inner join tb_broadcast_room_member t2 on t1.room_no = t2.room_no
where t2.mem_no = #{value}
    and t2.auth != 3
    and t2.state = 0;
