mapper.update:
        UPDATE tb_member_notification
			SET read_yn = 1
		WHERE mem_no = #{mem_no}

mapper.insert:
INSERT INTO rd_admin.tb_req_special_dj(
  mem_no
  , mem_name
  , mem_phone
  , title
  , contents
  , broadcast_time1
  , broadcast_time2
  , reg_date
  , state
) values (
  #{mem_no}
  , #{name}
  , #{phone}
  , #{title}
  , #{contents}
  , #{broadcast_time1}
  , #{broadcast_time2, jdbcType=VARCHAR}
  , NOW()
  , 1
)

mapper.select:
/* Mypage.xml - selectSpecialDjBroadcastTime */
select TRUNCATE(ifnull(sum(airtime), 0) / 3600, 0) airtime
from rd_data.tb_broadcast_room
where mem_no = #{mem_no}
    and DATE_FORMAT(concat(#{condition_start_date}, ' 00:00:00'), '%Y-%m-%d %H:%i:%s') <= start_date
    and start_date <= DATE_FORMAT(concat(#{condition_end_date}, ' 23:59:59'), '%Y-%m-%d %H:%i:%s')

/* Mypage.xml - selectSpecialDjLikeCnt */
select count(*)
from rd_data.tb_broadcast_room_good
where dj_mem_no = #{mem_no}
    and DATE_FORMAT(concat(#{condition_start_date}, ' 00:00:00'), '%Y-%m-%d %H:%i:%s') <= last_upd_date
    and last_upd_date <= DATE_FORMAT(concat(#{condition_end_date}, ' 23:59:59'), '%Y-%m-%d %H:%i:%s')

/* Mypage.xml - selectMemberFanCnt */
select count(*)
from rd_data.tb_member_fanstar
where mem_no_star = #{mem_no}

/* Mypage.xml - selectMemberLevel */
select count(*)
from rd_data.tb_member_broadcast_listening
where dj_mem_no = #{mem_no}
    and DATE_FORMAT(concat(#{condition_start_date}, ' 00:00:00'), '%Y-%m-%d %H:%i:%s') <= start_date
    and start_date <= DATE_FORMAT(concat(#{condition_end_date}, ' 23:59:59'), '%Y-%m-%d %H:%i:%s')
